// Parse and validate content generation request
const input = $input.first().json;

// Default parameters
const defaults = {
  platform: 'fanvue',
  content_type: 'lifestyle',
  count: 3,
  style: 'authentic',
  mood: 'positive',
  include_image: true,
  quality_threshold: 3.5
};

// Merge with request parameters
const params = {
  ...defaults,
  ...input.body,
  ...input.query,
  timestamp: new Date().toISOString(),
  request_id: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

// Validate platform
const validPlatforms = ['fanvue', 'loyalfans', 'instagram', 'twitter', 'tiktok'];
if (!validPlatforms.includes(params.platform)) {
  return {
    json: {
      error: 'Invalid platform',
      valid_platforms: validPlatforms,
      provided: params.platform
    }
  };
}

// Validate content type
const validTypes = ['lifestyle', 'fashion', 'fitness', 'motivation', 'behind_scenes', 'personal', 'teaser'];
if (!validTypes.includes(params.content_type)) {
  params.content_type = 'lifestyle'; // Default fallback
}

// Validate count
params.count = Math.min(Math.max(parseInt(params.count) || 3, 1), 10);

return { json: params };

// Intelligent content strategy based on learning data
const axios = require('axios');
const params = $input.first().json;

if (params.error) {
  return { json: params };
}

try {
  // Get AI insights for optimal content strategy
  const strategyResponse = await axios.post('http://localhost:5000/api/ai/content-strategy', {
    platform: params.platform,
    content_type: params.content_type,
    current_time: new Date().getHours(),
    day_of_week: new Date().getDay(),
    recent_performance: true
  });

  const strategy = strategyResponse.data;

  // Generate content prompts based on strategy
  const contentPrompts = [];

  for (let i = 0; i < params.count; i++) {
    const prompt = {
      id: `${params.request_id}_${i + 1}`,
      platform: params.platform,
      type: params.content_type,
      style: strategy.recommended_style || params.style,
      mood: strategy.recommended_mood || params.mood,
      themes: strategy.trending_themes || ['lifestyle', 'authentic'],
      hashtags: strategy.optimal_hashtags || [],
      image_style: strategy.image_style || 'lifestyle',
      caption_length: strategy.optimal_caption_length || 'medium',
      call_to_action: strategy.recommended_cta || 'engagement',
      personalization: strategy.personalization_elements || {}
    };

    // Add time-specific elements
    const currentHour = new Date().getHours();
    if (currentHour >= 6 && currentHour <= 9) {
      prompt.time_context = 'morning';
      prompt.themes.push('motivation', 'new_day');
    } else if (currentHour >= 12 && currentHour <= 14) {
      prompt.time_context = 'afternoon';
      prompt.themes.push('lifestyle', 'current_activity');
    } else if (currentHour >= 17 && currentHour <= 20) {
      prompt.time_context = 'evening';
      prompt.themes.push('reflection', 'relaxation');
    } else {
      prompt.time_context = 'late';
      prompt.themes.push('intimate', 'personal');
    }

    contentPrompts.push(prompt);
  }

  return {
    json: {
      strategy: strategy,
      prompts: contentPrompts,
      original_params: params,
      generation_plan: {
        total_content: params.count,
        estimated_time: params.count * 30, // seconds
        quality_checks: true,
        cross_platform_optimization: strategy.cross_platform || false
      }
    }
  };

} catch (error) {
  console.error('Strategy generation failed:', error);

  // Fallback strategy
  const fallbackPrompts = [];
  for (let i = 0; i < params.count; i++) {
    fallbackPrompts.push({
      id: `${params.request_id}_${i + 1}`,
      platform: params.platform,
      type: params.content_type,
      style: params.style,
      mood: params.mood,
      themes: ['lifestyle'],
      fallback: true
    });
  }

  return {
    json: {
      strategy: { fallback: true },
      prompts: fallbackPrompts,
      original_params: params,
      warning: 'Using fallback strategy due to AI strategy failure'
    }
  };
}

// Generate AI images for content
const axios = require('axios');
const data = $input.first().json;

if (data.error) {
  return { json: data };
}

const imageGeneration = {
  request_id: data.original_params.request_id,
  images: [],
  generation_time: 0,
  total_prompts: data.prompts.length
};

const startTime = Date.now();

try {
  // Generate images for each prompt
  for (const prompt of data.prompts) {
    if (!data.original_params.include_image) {
      // Skip image generation if not requested
      imageGeneration.images.push({
        id: prompt.id,
        image_path: null,
        placeholder: true,
        message: 'Image generation skipped by request'
      });
      continue;
    }

    const imagePrompt = {
      style: prompt.image_style,
      mood: prompt.mood,
      themes: prompt.themes,
      platform: prompt.platform,
      quality: 'high',
      size: data.original_params.platform === 'instagram' ? [1080, 1080] : [768, 768]
    };

    try {
      const imageResponse = await axios.post('http://localhost:5000/api/ai/generate-image', {
        prompt: imagePrompt,
        id: prompt.id
      });

      imageGeneration.images.push({
        id: prompt.id,
        image_path: imageResponse.data.image_path,
        image_url: imageResponse.data.image_url,
        generation_time: imageResponse.data.generation_time,
        style_applied: imageResponse.data.style,
        success: true
      });

    } catch (imageError) {
      console.error(`Image generation failed for ${prompt.id}:`, imageError);

      // Generate placeholder
      imageGeneration.images.push({
        id: prompt.id,
        image_path: `output/placeholder_${prompt.id}.png`,
        placeholder: true,
        error: imageError.message,
        success: false
      });
    }
  }

  imageGeneration.generation_time = Date.now() - startTime;
  imageGeneration.success = true;

  return {
    json: {
      ...data,
      images: imageGeneration
    }
  };

} catch (error) {
  console.error('Batch image generation failed:', error);
  return {
    json: {
      ...data,
      images: {
        ...imageGeneration,
        error: error.message,
        success: false
      }
    }
  };
}

// Generate AI-powered captions
const axios = require('axios');
const data = $input.first().json;

if (data.error) {
  return { json: data };
}

const captionGeneration = {
  request_id: data.original_params.request_id,
  captions: [],
  generation_time: 0,
  total_prompts: data.prompts.length
};

const startTime = Date.now();

try {
  // Generate captions for each prompt
  for (const prompt of data.prompts) {
    const captionParams = {
      platform: prompt.platform,
      content_type: prompt.type,
      style: prompt.style,
      mood: prompt.mood,
      themes: prompt.themes,
      time_context: prompt.time_context,
      hashtags: prompt.hashtags,
      call_to_action: prompt.call_to_action,
      length: prompt.caption_length,
      personalization: prompt.personalization
    };

    try {
      const captionResponse = await axios.post('http://localhost:5000/api/ai/generate-caption', {
        params: captionParams,
        id: prompt.id
      });

      captionGeneration.captions.push({
        id: prompt.id,
        caption: captionResponse.data.caption,
        hashtags: captionResponse.data.hashtags,
        word_count: captionResponse.data.word_count,
        estimated_engagement: captionResponse.data.estimated_engagement,
        quality_score: captionResponse.data.quality_score,
        platform_optimized: captionResponse.data.platform_optimized,
        success: true
      });

    } catch (captionError) {
      console.error(`Caption generation failed for ${prompt.id}:`, captionError);

      // Generate fallback caption
      const fallbackCaption = generateFallbackCaption(prompt);

      captionGeneration.captions.push({
        id: prompt.id,
        caption: fallbackCaption.caption,
        hashtags: fallbackCaption.hashtags,
        word_count: fallbackCaption.caption.split(' ').length,
        quality_score: 3.0,
        fallback: true,
        error: captionError.message,
        success: false
      });
    }
  }

  captionGeneration.generation_time = Date.now() - startTime;
  captionGeneration.success = true;

  return {
    json: {
      ...data,
      captions: captionGeneration
    }
  };

} catch (error) {
  console.error('Batch caption generation failed:', error);
  return {
    json: {
      ...data,
      captions: {
        ...captionGeneration,
        error: error.message,
        success: false
      }
    }
  };
}

// Fallback caption generator
function generateFallbackCaption(prompt) {
  const templates = {
    lifestyle: {
      fanvue: "Living my best life and sharing authentic moments with you ðŸ’« What's making you smile today?",
      loyalfans: "Exclusive glimpse into my world âœ¨ Premium content for my favorite people."
    },
    fashion: {
      fanvue: "Today's vibe: confident and stylish ðŸ‘— Fashion is about feeling good in your own skin.",
      loyalfans: "Curated style just for you ðŸ’Ž Elegance meets authenticity."
    },
    fitness: {
      fanvue: "Strength comes from within ðŸ’ª Sharing my fitness journey with amazing people like you.",
      loyalfans: "Exclusive workout content and wellness tips ðŸƒâ€â™€ï¸ VIP access to my fitness routine."
    }
  };

  const template = templates[prompt.type]?.[prompt.platform] || 
    "Sharing a special moment with you âœ¨ Thank you for being part of this journey.";

  const hashtags = {
    fanvue: "#fanvue #authentic #lifestyle #realme #genuine",
    loyalfans: "#loyalfans #exclusive #premium #vip #content"
  }[prompt.platform] || "#content #lifestyle #authentic";

  return {
    caption: `${template}\n\n${hashtags}`,
    hashtags: hashtags.split(' ')
  };
}

// Comprehensive quality assessment
const axios = require('axios');
const data = $input.first().json;

if (data.error) {
  return { json: data };
}

const qualityAssessment = {
  request_id: data.original_params.request_id,
  assessments: [],
  overall_quality: 0,
  passed_threshold: 0,
  failed_threshold: 0
};

const qualityThreshold = data.original_params.quality_threshold || 3.5;

try {
  // Assess each piece of content
  for (let i = 0; i < data.prompts.length; i++) {
    const prompt = data.prompts[i];
    const image = data.images?.images?.[i];
    const caption = data.captions?.captions?.[i];

    if (!caption) {
      console.error(`No caption found for prompt ${prompt.id}`);
      continue;
    }

    try {
      const assessmentResponse = await axios.post('http://localhost:5000/api/ai/assess-quality', {
        content: {
          caption: caption.caption,
          hashtags: caption.hashtags,
          image_available: image?.success || false,
          platform: prompt.platform,
          content_type: prompt.type
        },
        id: prompt.id
      });

      const assessment = {
        id: prompt.id,
        overall_score: assessmentResponse.data.overall_score,
        readability: assessmentResponse.data.readability,
        engagement_potential: assessmentResponse.data.engagement_potential,
        platform_fit: assessmentResponse.data.platform_fit,
        quality_level: assessmentResponse.data.quality_level,
        strengths: assessmentResponse.data.strengths,
        improvements: assessmentResponse.data.improvements,
        recommendations: assessmentResponse.data.recommendations,
        passed_threshold: assessmentResponse.data.overall_score >= qualityThreshold,
        success: true
      };

      qualityAssessment.assessments.push(assessment);

      if (assessment.passed_threshold) {
        qualityAssessment.passed_threshold++;
      } else {
        qualityAssessment.failed_threshold++;
      }

    } catch (assessmentError) {
      console.error(`Quality assessment failed for ${prompt.id}:`, assessmentError);

      // Fallback assessment
      const fallbackAssessment = {
        id: prompt.id,
        overall_score: caption.quality_score || 3.0,
        quality_level: 'SATISFACTORY',
        passed_threshold: (caption.quality_score || 3.0) >= qualityThreshold,
        fallback: true,
        error: assessmentError.message,
        success: false
      };

      qualityAssessment.assessments.push(fallbackAssessment);

      if (fallbackAssessment.passed_threshold) {
        qualityAssessment.passed_threshold++;
      } else {
        qualityAssessment.failed_threshold++;
      }
    }
  }

  // Calculate overall quality
  if (qualityAssessment.assessments.length > 0) {
    const totalScore = qualityAssessment.assessments.reduce((sum, assessment) => 
      sum + (assessment.overall_score || 3.0), 0);
    qualityAssessment.overall_quality = totalScore / qualityAssessment.assessments.length;
  }

  qualityAssessment.success = true;
  qualityAssessment.batch_passed = qualityAssessment.passed_threshold >= Math.ceil(data.prompts.length * 0.7); // 70% pass rate

  return {
    json: {
      ...data,
      quality: qualityAssessment
    }
  };

} catch (error) {
  console.error('Quality assessment batch failed:', error);
  return {
    json: {
      ...data,
      quality: {
        ...qualityAssessment,
        error: error.message,
        success: false
      }
    }
  };
}

// Compile final content packages
const data = $input.first().json;

if (data.error) {
  return { json: data };
}

const finalContent = {
  request_id: data.original_params.request_id,
  platform: data.original_params.platform,
  content_type: data.original_params.content_type,
  generated_at: new Date().toISOString(),
  total_generated: data.prompts.length,
  quality_passed: data.quality?.passed_threshold || 0,
  overall_quality: data.quality?.overall_quality || 0,
  content_items: []
};

// Compile each content item
for (let i = 0; i < data.prompts.length; i++) {
  const prompt = data.prompts[i];
  const image = data.images?.images?.[i];
  const caption = data.captions?.captions?.[i];
  const quality = data.quality?.assessments?.[i];

  const contentItem = {
    id: prompt.id,
    platform: prompt.platform,
    content_type: prompt.type,

    // Content
    caption: caption?.caption || 'Caption generation failed',
    hashtags: caption?.hashtags || [],
    word_count: caption?.word_count || 0,

    // Image
    image_path: image?.image_path || null,
    image_url: image?.image_url || null,
    image_generated: image?.success || false,

    // Quality metrics
    quality_score: quality?.overall_score || caption?.quality_score || 3.0,
    quality_level: quality?.quality_level || 'SATISFACTORY',
    passed_threshold: quality?.passed_threshold || false,

    // Metadata
    style: prompt.style,
    mood: prompt.mood,
    themes: prompt.themes,
    time_context: prompt.time_context,
    estimated_engagement: caption?.estimated_engagement || 'medium',

    // Quality details
    strengths: quality?.strengths || [],
    improvements: quality?.improvements || [],
    recommendations: quality?.recommendations || [],

    // Generation status
    caption_success: caption?.success || false,
    image_success: image?.success || false,
    quality_success: quality?.success || false,

    // Errors (if any)
    errors: {
      caption: caption?.success === false ? caption?.error : null,
      image: image?.success === false ? image?.error : null,
      quality: quality?.success === false ? quality?.error : null
    }
  };

  finalContent.content_items.push(contentItem);
}

// Add summary statistics
finalContent.statistics = {
  generation_time: {
    images: data.images?.generation_time || 0,
    captions: data.captions?.generation_time || 0,
    total: (data.images?.generation_time || 0) + (data.captions?.generation_time || 0)
  },
  success_rates: {
    images: data.images?.images ? data.images.images.filter(img => img.success).length / data.images.images.length : 0,
    captions: data.captions?.captions ? data.captions.captions.filter(cap => cap.success).length / data.captions.captions.length : 0,
    quality: data.quality?.assessments ? data.quality.assessments.filter(qual => qual.success).length / data.quality.assessments.length : 0
  },
  average_quality: finalContent.overall_quality,
  content_ready_to_post: finalContent.content_items.filter(item => item.passed_threshold).length
};

// Add recommendations for failed content
finalContent.batch_recommendations = [];
if (finalContent.quality_passed < finalContent.total_generated * 0.8) {
  finalContent.batch_recommendations.push('Consider regenerating content with adjusted parameters for better quality');
}
if (finalContent.statistics.success_rates.images < 0.8) {
  finalContent.batch_recommendations.push('Image generation had issues - check AI model status');
}
if (finalContent.statistics.success_rates.captions < 0.8) {
  finalContent.batch_recommendations.push('Caption generation had issues - verify text generation service');
}

return { json: finalContent };

// Format response for webhook
const content = $input.first().json;

// Create response payload
const response = {
  success: true,
  request_id: content.request_id,
  generated_at: content.generated_at,
  platform: content.platform,
  content_type: content.content_type,

  summary: {
    total_generated: content.total_generated,
    quality_passed: content.quality_passed,
    average_quality: parseFloat(content.overall_quality.toFixed(2)),
    ready_to_post: content.content_ready_to_post
  },

  content: content.content_items.map(item => ({
    id: item.id,
    caption: item.caption,
    hashtags: item.hashtags,
    image_path: item.image_path,
    quality_score: parseFloat(item.quality_score.toFixed(2)),
    quality_level: item.quality_level,
    passed_threshold: item.passed_threshold,
    estimated_engagement: item.estimated_engagement,
    recommendations: item.recommendations.slice(0, 3) // Limit to top 3
  })),

  statistics: content.statistics,
  recommendations: content.batch_recommendations,

  meta: {
    generation_time_ms: content.statistics.generation_time.total,
    ai_strategy_used: !content.strategy?.fallback,
    webhook_response: true
  }
};

// Add errors if any critical failures
const criticalErrors = content.content_items.filter(item => 
  !item.caption_success && !item.image_success
);

if (criticalErrors.length > 0) {
  response.warnings = [`${criticalErrors.length} content items had critical generation failures`];
}

return { json: response };
